export class chartCap implements OnDestroy,OnChanges {
  options: any = {};
  themeSubscription: any;
  @Input() dataGrid;
  @Input() eventPages;
  @Input() eventExploitation;
  @Input() eventCapteur;
  maximum;
  minimum;
  derniere_mesure;
  id_capteur;
  mesure;
 
  constructor(private theme: NbThemeService) {}
  ngOnChanges() {
    this.dataGrid.forEach(element => {
            
      if( element.mesure == this.eventPages && element.id_capteur == this.eventCapteur ){
          
        this.maximum = element.maximum_24h;
        this.minimum = element.minimum_24h;
        this.derniere_mesure = element.derniere_mesure.value;
        this.id_capteur = element.id_capteur;
        this.mesure = element.mesure;
         
      }
  });
    this.themeSubscription = this.theme.getJsTheme().subscribe(config => {

      const colors = config.variables;
      const echarts: any = config.variables.echarts;

      this.options = {
        tooltip: {
            formatter: '{a} <br/>{b} : {c} C°'
        },
       
        series: [
          {
            min : -5,
            max : 45,
              name: 'Temperature C°',
              type: 'gauge',
                axisLine: {
          lineStyle: {
              width: 30,
              color: [
                  [0.2, '#0095ff'],
                  [0.7, '#41cc5d'],
                  [1, '#ff3d71']
              ]
          }
      },
              detail: {formatter: '{value} C°'},
              data: [{value: this.derniere_mesure, name: this.id_capteur}]
          }
      ]
    };
    
    });
  }

  ngOnDestroy(): void {
    this.themeSubscription.unsubscribe();
  }
}
